import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";

export default function Dashboard() {
  const [consultasHoje, setConsultasHoje] = useState(0);
  const [consultasPorHora, setConsultasPorHora] = useState([]);
  const [medicoNome, setMedicoNome] = useState(""); // Novo estado pro nome do médico
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Carregar dados do dashboard e nome do médico
  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("Você precisa estar logado.");
        setTimeout(() => navigate("/login"), 2000);
        return;
      }

      try {
        // Verificar token e pegar nome do médico
        const verifyResponse = await fetch("http://localhost:5000/api/medicos/verify", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        });
        const verifyData = await verifyResponse.json();
        if (!verifyResponse.ok) {
          throw new Error("Token inválido.");
        }
        setMedicoNome(verifyData.nome); // O endpoint /verify retorna o nome do médico

        // Buscar consultas do dia
        const todayResponse = await fetch("http://localhost:5000/api/consultas/today", {
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        });
        const todayData = await todayResponse.json();
        if (todayResponse.ok) {
          setConsultasHoje(todayData.count);
        } else {
          setError(todayData.message || "Erro ao carregar consultas do dia");
        }

        // Buscar consultas por hora
        const hourlyResponse = await fetch("http://localhost:5000/api/consultas/hourly", {
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        });
        const hourlyData = await hourlyResponse.json();
        if (hourlyResponse.ok) {
          // Preencher todas as horas de 0 a 23
          const allHours = Array.from({ length: 24 }, (_, i) => ({
            hora: i,
            count: 0,
          }));
          hourlyData.forEach((data) => {
            allHours[data.hora].count = data.count;
          });
          setConsultasPorHora(allHours);
        } else {
          setError(hourlyData.message || "Erro ao carregar consultas por hora");
        }

        setLoading(false);
      } catch (err) {
        setError("Sessão expirada. Faça login novamente.");
        localStorage.removeItem("token");
        setTimeout(() => navigate("/login"), 2000);
      }
    };

    fetchData();
  }, [navigate]);

  // Função de logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <p className="text-gray-600 text-xl">Carregando...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="bg-red-100 text-red-700 p-4 rounded-md">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-md p-4 flex justify-between items-center">
        <div className="flex items-center">
          <svg
            className="w-8 h-8 text-green-500 mr-2"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
            />
          </svg>
          <h1 className="text-2xl font-semibold text-gray-800">CardioRisk</h1>
        </div>
        <button
          onClick={handleLogout}
          className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition duration-200"
        >
          Sair
        </button>
      </header>

      {/* Conteúdo Principal */}
      <div className="p-8">
        <div className="max-w-5xl mx-auto">
          <div className="bg-white rounded-lg shadow-md p-6 mb-6">
            <div className="flex justify-between items-center mb-6">
              <h1 className="text-3xl font-bold text-green-700">
                Bem-vindo, {medicoNome || "Médico"} {/* Exibe o nome do médico */}
              </h1>
              <button
                onClick={() => navigate("/calcular-risco")}
                className="bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600 transition duration-200"
              >
                Calcular Novo Risco
              </button>
            </div>

            {/* Consultas Hoje */}
            <div className="mb-6">
              <h2 className="text-xl font-semibold text-gray-800 mb-2">
                Consultas Hoje
              </h2>
              <p className="text-gray-600 text-3xl">{consultasHoje}</p>
            </div>

            {/* Gráfico de Consultas por Hora */}
            <div>
              <h2 className="text-xl font-semibold text-gray-800 mb-4">
                Consultas por Hora
              </h2>
              <div className="h-80">
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart
                    data={consultasPorHora}
                    margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="hora" />
                    <YAxis />
                    <Tooltip />
                    <Bar dataKey="count" fill="#10B981" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
