import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Pacientes() {
  const [pacientes, setPacientes] = useState([]);
  const [nome, setNome] = useState("");
  const [idade, setIdade] = useState("");
  const [sexo, setSexo] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Verificar token
  useEffect(() => {
    const verifyToken = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError("Você precisa estar logado.");
        setTimeout(() => navigate("/login"), 2000);
        return;
      }

      try {
        const response = await fetch("http://localhost:5000/api/medicos/verify", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error("Token inválido.");
        }
        setLoading(false);
        fetchPacientes(token);
      } catch (err) {
        setError("Sessão expirada. Faça login novamente.");
        localStorage.removeItem('token');
        setTimeout(() => navigate("/login"), 2000);
      }
    };

    verifyToken();
  }, [navigate]);

  // Buscar pacientes
  const fetchPacientes = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/pacientes", {
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setPacientes(data);
      } else {
        setError(data.message || "Erro ao carregar pacientes");
      }
    } catch (err) {
      setError("Erro ao conectar ao servidor");
    }
  };

  // Adicionar paciente
  const handleAddPaciente = async (e) => {
    e.preventDefault();
    setError("");
    const token = localStorage.getItem('token');
    try {
      const response = await fetch("http://localhost:5000/api/pacientes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify({ nome, idade, sexo }),
      });
      const data = await response.json();
      if (response.ok) {
        setPacientes([...pacientes, data]);
        setNome("");
        setIdade("");
        setSexo("");
      } else {
        setError(data.message || "Erro ao adicionar paciente");
      }
    } catch (err) {
      setError("Erro ao conectar ao servidor");
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-teal-400">
        <p className="text-white text-xl">Carregando...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-teal-400">
        <div className="bg-red-100 text-red-700 p-4 rounded-md">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-500 to-teal-400 p-8">
      <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">Gerenciar Pacientes</h1>
          <button
            onClick={() => navigate("/dashboard")}
            className="bg-gray-600 text-white py-2 px-4 rounded-md hover:bg-gray-700 transition duration-200"
          >
            Voltar ao Dashboard
          </button>
        </div>
        <form onSubmit={handleAddPaciente} className="mb-6 space-y-4">
          <div>
            <label htmlFor="nome" className="block text-sm font-medium text-gray-700">
              Nome
            </label>
            <input
              id="nome"
              type="text"
              className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              value={nome}
              onChange={(e) => setNome(e.target.value)}
              placeholder="Nome do paciente"
              required
            />
          </div>
          <div>
            <label htmlFor="idade" className="block text-sm font-medium text-gray-700">
              Idade
            </label>
            <input
              id="idade"
              type="number"
              className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              value={idade}
              onChange={(e) => setIdade(e.target.value)}
              placeholder="Idade"
              required
            />
          </div>
          <div>
            <label htmlFor="sexo" className="block text-sm font-medium text-gray-700">
              Sexo
            </label>
            <select
              id="sexo"
              className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              value={sexo}
              onChange={(e) => setSexo(e.target.value)}
              required
            >
              <option value="">Selecione</option>
              <option value="Masculino">Masculino</option>
              <option value="Feminino">Feminino</option>
            </select>
          </div>
          <button
            type="submit"
            className="bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 transition duration-200"
          >
            Adicionar Paciente
          </button>
        </form>
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Lista de Pacientes</h2>
        {pacientes.length === 0 ? (
          <p className="text-gray-600">Nenhum paciente cadastrado.</p>
        ) : (
          <ul className="space-y-2">
            {pacientes.map((paciente) => (
              <li key={paciente._id} className="p-4 bg-gray-100 rounded-md">
                <p><strong>Nome:</strong> {paciente.nome}</p>
                <p><strong>Idade:</strong> {paciente.idade}</p>
                <p><strong>Sexo:</strong> {paciente.sexo}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
